import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { motion, AnimatePresence } from "framer-motion";
import { Plus, Wifi, Server, Shield } from "lucide-react";
import { GlassPanel } from "@/components/glass-panel";
import { GlassInput, GlassTextarea } from "@/components/glass-input";
import { HotspotCard, HotspotCardSkeleton, AddHotspotButton } from "@/components/hotspot-card";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { Hotspot, InsertHotspot } from "@shared/schema";

export default function HotspotsPage() {
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingHotspot, setEditingHotspot] = useState<Hotspot | null>(null);
  const [deletingHotspot, setDeletingHotspot] = useState<Hotspot | null>(null);

  // Form state
  const [formData, setFormData] = useState<Partial<InsertHotspot>>({
    locationName: "",
    description: "",
    nasIp: "",
    secret: "",
  });

  // Fetch hotspots
  const { data: hotspots, isLoading } = useQuery<Hotspot[]>({
    queryKey: ["/api/hotspots"],
  });

  // Create/Update mutation
  const saveMutation = useMutation({
    mutationFn: async (data: Partial<InsertHotspot>) => {
      if (editingHotspot) {
        return apiRequest("PATCH", `/api/hotspots/${editingHotspot.id}`, data);
      }
      return apiRequest("POST", "/api/hotspots", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotspots"] });
      toast({
        title: editingHotspot ? "Hotspot Updated" : "Hotspot Created",
        description: `The hotspot has been ${editingHotspot ? "updated" : "created"} successfully.`,
      });
      handleCloseDialog();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save hotspot",
        variant: "destructive",
      });
    },
  });

  // Delete mutation
  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/hotspots/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotspots"] });
      toast({
        title: "Hotspot Deleted",
        description: "The hotspot has been deleted successfully.",
      });
      setDeletingHotspot(null);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete hotspot",
        variant: "destructive",
      });
    },
  });

  // Toggle active mutation
  const toggleMutation = useMutation({
    mutationFn: async (hotspot: Hotspot) => {
      return apiRequest("PATCH", `/api/hotspots/${hotspot.id}`, {
        isActive: !hotspot.isActive,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotspots"] });
      toast({
        title: "Hotspot Updated",
        description: "The hotspot status has been updated.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update hotspot",
        variant: "destructive",
      });
    },
  });

  const handleOpenDialog = (hotspot?: Hotspot) => {
    if (hotspot) {
      setEditingHotspot(hotspot);
      setFormData({
        locationName: hotspot.locationName,
        description: hotspot.description || "",
        nasIp: hotspot.nasIp,
        secret: hotspot.secret,
      });
    } else {
      setEditingHotspot(null);
      setFormData({
        locationName: "",
        description: "",
        nasIp: "",
        secret: "",
      });
    }
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
    setEditingHotspot(null);
    setFormData({
      locationName: "",
      description: "",
      nasIp: "",
      secret: "",
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    saveMutation.mutate(formData);
  };

  // Generate random secret
  const generateSecret = () => {
    const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    let secret = "";
    for (let i = 0; i < 16; i++) {
      secret += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    setFormData({ ...formData, secret });
  };

  return (
    <div className="p-6 space-y-6" data-testid="hotspots-page">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-white">Hotspots</h1>
          <p className="text-muted-foreground">
            Manage your hotspot locations and NAS devices
          </p>
        </div>
        <Button
          className="gradient-btn"
          onClick={() => handleOpenDialog()}
          data-testid="button-add-hotspot"
        >
          <Plus size={18} className="mr-2" />
          Add Hotspot
        </Button>
      </div>

      {/* Hotspots Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {isLoading ? (
          <>
            <HotspotCardSkeleton />
            <HotspotCardSkeleton />
            <HotspotCardSkeleton />
          </>
        ) : hotspots && hotspots.length > 0 ? (
          <AnimatePresence mode="popLayout">
            {hotspots.map((hotspot) => (
              <HotspotCard
                key={hotspot.id}
                hotspot={hotspot}
                onEdit={handleOpenDialog}
                onDelete={setDeletingHotspot}
                onToggleActive={(h) => toggleMutation.mutate(h)}
              />
            ))}
            <AddHotspotButton onClick={() => handleOpenDialog()} />
          </AnimatePresence>
        ) : (
          <GlassPanel className="col-span-full text-center py-12">
            <Wifi size={48} className="mx-auto mb-4 text-muted-foreground opacity-50" />
            <p className="text-muted-foreground mb-4">No hotspots configured yet</p>
            <Button
              className="gradient-btn"
              onClick={() => handleOpenDialog()}
              data-testid="button-create-first-hotspot"
            >
              Add Your First Hotspot
            </Button>
          </GlassPanel>
        )}
      </div>

      {/* Create/Edit Dialog */}
      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="glass-panel border-white/10 max-w-lg">
          <DialogHeader>
            <DialogTitle className="text-xl font-bold text-white">
              {editingHotspot ? "Edit Hotspot" : "Add New Hotspot"}
            </DialogTitle>
          </DialogHeader>

          <form onSubmit={handleSubmit} className="space-y-6">
            <GlassInput
              label="Location Name"
              placeholder="e.g., Main Lobby, Cafe Area"
              value={formData.locationName}
              onChange={(e) => setFormData({ ...formData, locationName: e.target.value })}
              required
              data-testid="input-hotspot-name"
            />

            <GlassTextarea
              label="Description"
              placeholder="Brief description of this location"
              value={formData.description || ""}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              data-testid="input-hotspot-description"
            />

            <GlassInput
              label="NAS IP Address"
              placeholder="e.g., 192.168.1.1"
              value={formData.nasIp}
              onChange={(e) => setFormData({ ...formData, nasIp: e.target.value })}
              icon={<Server size={16} />}
              required
              data-testid="input-hotspot-ip"
            />

            <div className="space-y-2">
              <GlassInput
                label="RADIUS Secret"
                placeholder="Enter secret or generate one"
                value={formData.secret}
                onChange={(e) => setFormData({ ...formData, secret: e.target.value })}
                icon={<Shield size={16} />}
                required
                data-testid="input-hotspot-secret"
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                onClick={generateSecret}
                className="text-cyan-400 hover:text-cyan-300"
                data-testid="button-generate-secret"
              >
                Generate Random Secret
              </Button>
            </div>

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="ghost"
                onClick={handleCloseDialog}
                className="flex-1"
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                className="flex-1 gradient-btn"
                disabled={saveMutation.isPending}
                data-testid="button-save-hotspot"
              >
                {saveMutation.isPending ? "Saving..." : editingHotspot ? "Update Hotspot" : "Add Hotspot"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation */}
      <AlertDialog open={!!deletingHotspot} onOpenChange={() => setDeletingHotspot(null)}>
        <AlertDialogContent className="glass-panel border-white/10">
          <AlertDialogHeader>
            <AlertDialogTitle className="text-white">Delete Hotspot</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete "{deletingHotspot?.locationName}"? This action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-delete">Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => deletingHotspot && deleteMutation.mutate(deletingHotspot.id)}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
              data-testid="button-confirm-delete"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

client/src/components/mnetifi-logo.tsx.content:
import { motion } from "framer-motion";
import { Wifi } from "lucide-react";
import { cn } from "@/lib/utils";

interface MnetiFiLogoProps {
  size?: "sm" | "md" | "lg" | "xl";
  showText?: boolean;
  className?: string;
}

export function MnetiFiLogo({ size = "md", showText = true, className }: MnetiFiLogoProps) {
  const sizeConfig = {
    sm: { icon: 20, text: "text-lg", gap: "gap-2" },
    md: { icon: 28, text: "text-2xl", gap: "gap-2" },
    lg: { icon: 36, text: "text-3xl", gap: "gap-3" },
    xl: { icon: 48, text: "text-4xl", gap: "gap-4" },
  };

  const config = sizeConfig[size];

  return (
    <motion.div
      className={cn("flex items-center", config.gap, className)}
      initial={{ opacity: 0, y: -10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      data-testid="mnetifi-logo"
    >
      <motion.div
        className="relative"
        whileHover={{ scale: 1.05 }}
        transition={{ type: "spring", stiffness: 400 }}
      >
        {/* Gradient glow behind icon */}
        <div
          className="absolute inset-0 rounded-full blur-lg opacity-60"
          style={{
            background: "linear-gradient(135deg, #06b6d4 0%, #3b82f6 100%)",
          }}
        />
        <div
          className="relative flex items-center justify-center rounded-xl p-2"
          style={{
            background: "linear-gradient(135deg, rgba(6, 182, 212, 0.2) 0%, rgba(59, 130, 246, 0.2) 100%)",
            border: "1px solid rgba(6, 182, 212, 0.3)",
          }}
        >
          <Wifi
            size={config.icon}
            className="text-cyan-400"
            strokeWidth={2.5}
          />
        </div>
      </motion.div>

      {showText && (
        <div className="flex flex-col">
          <span
            className={cn(
              "font-bold tracking-tight gradient-text",
              config.text
            )}
          >
            MnetiFi
          </span>
          {size !== "sm" && (
            <span className="text-xs text-muted-foreground tracking-wide uppercase">
              Wi-Fi Billing
            </span>
          )}
        </div>
      )}
    </motion.div>
  );
}

// M-Pesa Logo component
export function MpesaLogo({ size = 24 }: { size?: number }) {
  return (
    <svg
      width={size}
      height={size}
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      data-testid="mpesa-logo"
    >
      <circle cx="12" cy="12" r="11" fill="#4BB617" />
      <path
        d="M7 12L10 15L17 8"
        stroke="white"
        strokeWidth="2.5"
        strokeLinecap="round"
        strokeLinejoin="round"
      />
    </svg>
  );
}

client/src/pages/tickets.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { motion, AnimatePresence } from "framer-motion";
import { 
  Plus, 
  Ticket,
  Clock,
  Search,
  MoreVertical,
  MessageSquare,
  CheckCircle,
  AlertCircle,
  AlertTriangle,
  User,
} from "lucide-react";
import { GlassPanel } from "@/components/glass-panel";
import { GlassInput, GlassTextarea } from "@/components/glass-input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
  DropdownMenuSeparator,
} from "@/components/ui/dropdown-menu";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { cn } from "@/lib/utils";
import type { Ticket as TicketType, InsertTicket, WifiUser } from "@shared/schema";
import { formatDistanceToNow, format } from "date-fns";

const statusLabels = {
  OPEN: "Open",
  IN_PROGRESS: "In Progress",
  RESOLVED: "Resolved",
  CLOSED: "Closed",
};

const statusColors = {
  OPEN: "bg-blue-500/20 text-blue-400 border-blue-500/30",
  IN_PROGRESS: "bg-amber-500/20 text-amber-400 border-amber-500/30",
  RESOLVED: "bg-emerald-500/20 text-emerald-400 border-emerald-500/30",
  CLOSED: "bg-gray-500/20 text-gray-400 border-gray-500/30",
};

const priorityLabels = {
  LOW: "Low",
  MEDIUM: "Medium",
  HIGH: "High",
  URGENT: "Urgent",
};

const priorityColors = {
  LOW: "bg-gray-500/20 text-gray-400 border-gray-500/30",
  MEDIUM: "bg-blue-500/20 text-blue-400 border-blue-500/30",
  HIGH: "bg-amber-500/20 text-amber-400 border-amber-500/30",
  URGENT: "bg-red-500/20 text-red-400 border-red-500/30",
};

const priorityIcons = {
  LOW: AlertCircle,
  MEDIUM: AlertCircle,
  HIGH: AlertTriangle,
  URGENT: AlertTriangle,
};

export default function TicketsPage() {
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [isCloseDialogOpen, setIsCloseDialogOpen] = useState(false);
  const [editingTicket, setEditingTicket] = useState<TicketType | null>(null);
  const [closingTicket, setClosingTicket] = useState<TicketType | null>(null);
  const [resolutionNotes, setResolutionNotes] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [filterStatus, setFilterStatus] = useState<string>("all");
  const [filterPriority, setFilterPriority] = useState<string>("all");

  const [formData, setFormData] = useState<Partial<InsertTicket>>({
    subject: "",
    issueDetails: "",
    priority: "MEDIUM",
    wifiUserId: "",
  });

  const { data: ticketsList, isLoading } = useQuery<TicketType[]>({
    queryKey: ["/api/tickets"],
  });

  const { data: wifiUsers } = useQuery<WifiUser[]>({
    queryKey: ["/api/wifi-users"],
  });

  const saveMutation = useMutation({
    mutationFn: async (data: Partial<InsertTicket>) => {
      if (editingTicket) {
        return apiRequest("PATCH", `/api/tickets/${editingTicket.id}`, data);
      }
      return apiRequest("POST", "/api/tickets", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/tickets"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: editingTicket ? "Ticket Updated" : "Ticket Created",
        description: `Ticket has been ${editingTicket ? "updated" : "created"} successfully.`,
      });
      handleCloseDialog();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save ticket",
        variant: "destructive",
      });
    },
  });

  const closeMutation = useMutation({
    mutationFn: async ({ id, resolutionNotes }: { id: string; resolutionNotes: string }) => {
      return apiRequest("POST", `/api/tickets/${id}/close`, { resolutionNotes });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/tickets"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: "Ticket Closed",
        description: "Ticket has been closed successfully.",
      });
      setIsCloseDialogOpen(false);
      setClosingTicket(null);
      setResolutionNotes("");
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to close ticket",
        variant: "destructive",
      });
    },
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      return apiRequest("PATCH", `/api/tickets/${id}`, { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/tickets"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: "Status Updated",
        description: "Ticket status has been updated.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update status",
        variant: "destructive",
      });
    },
  });

  const handleOpenDialog = (ticket?: TicketType) => {
    if (ticket) {
      setEditingTicket(ticket);
      setFormData({
        subject: ticket.subject,
        issueDetails: ticket.issueDetails,
        priority: ticket.priority,
        wifiUserId: ticket.wifiUserId || "",
      });
    } else {
      setEditingTicket(null);
      setFormData({
        subject: "",
        issueDetails: "",
        priority: "MEDIUM",
        wifiUserId: "",
      });
    }
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
    setEditingTicket(null);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const dataToSubmit = {
      ...formData,
      wifiUserId: formData.wifiUserId || null,
    };
    saveMutation.mutate(dataToSubmit);
  };

  const handleOpenCloseDialog = (ticket: TicketType) => {
    setClosingTicket(ticket);
    setResolutionNotes(ticket.resolutionNotes || "");
    setIsCloseDialogOpen(true);
  };

  const filteredTickets = ticketsList?.filter((ticket) => {
    const matchesSearch = 
      ticket.subject.toLowerCase().includes(searchQuery.toLowerCase()) ||
      ticket.issueDetails.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = filterStatus === "all" || ticket.status === filterStatus;
    const matchesPriority = filterPriority === "all" || ticket.priority === filterPriority;

    return matchesSearch && matchesStatus && matchesPriority;
  });

  const wifiUsersMap = new Map(wifiUsers?.map((u) => [u.id, u]) || []);

  return (
    <div className="p-6 space-y-6" data-testid="tickets-page">
      <div className="flex items-center justify-between flex-wrap gap-4">
        <div>
          <h1 className="text-2xl font-bold text-white">Support Tickets</h1>
          <p className="text-muted-foreground">
            Track and manage customer support requests
          </p>
        </div>
        <Button
          className="gradient-btn"
          onClick={() => handleOpenDialog()}
          data-testid="button-add-ticket"
        >
          <Plus size={18} className="mr-2" />
          New Ticket
        </Button>
      </div>

      <GlassPanel size="sm" className="flex flex-wrap gap-4 items-center">
        <div className="relative flex-1 min-w-[200px]">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground" size={18} />
          <input
            type="text"
            placeholder="Search tickets..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-10 pr-4 py-2 bg-white/5 border border-white/10 rounded-lg text-white placeholder:text-muted-foreground focus:outline-none focus:ring-2 focus:ring-cyan-500/50"
            data-testid="input-search-tickets"
          />
        </div>

        <Select value={filterStatus} onValueChange={setFilterStatus}>
          <SelectTrigger className="w-[150px] bg-white/5 border-white/10" data-testid="select-ticket-status">
            <SelectValue placeholder="Status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Status</SelectItem>
            <SelectItem value="OPEN">Open</SelectItem>
            <SelectItem value="IN_PROGRESS">In Progress</SelectItem>
            <SelectItem value="RESOLVED">Resolved</SelectItem>
            <SelectItem value="CLOSED">Closed</SelectItem>
          </SelectContent>
        </Select>

        <Select value={filterPriority} onValueChange={setFilterPriority}>
          <SelectTrigger className="w-[130px] bg-white/5 border-white/10" data-testid="select-ticket-priority">
            <SelectValue placeholder="Priority" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Priority</SelectItem>
            <SelectItem value="LOW">Low</SelectItem>
            <SelectItem value="MEDIUM">Medium</SelectItem>
            <SelectItem value="HIGH">High</SelectItem>
            <SelectItem value="URGENT">Urgent</SelectItem>
          </SelectContent>
        </Select>
      </GlassPanel>

      <div className="space-y-4">
        {isLoading ? (
          <div className="space-y-4">
            {[1, 2, 3].map((i) => (
              <GlassPanel key={i} className="animate-pulse">
                <div className="space-y-3">
                  <div className="h-5 bg-white/10 rounded w-1/3" />
                  <div className="h-4 bg-white/10 rounded w-2/3" />
                </div>
              </GlassPanel>
            ))}
          </div>
        ) : filteredTickets && filteredTickets.length > 0 ? (
          <AnimatePresence mode="popLayout">
            {filteredTickets.map((ticket) => {
              const PriorityIcon = priorityIcons[ticket.priority as keyof typeof priorityIcons];
              const wifiUser = ticket.wifiUserId ? wifiUsersMap.get(ticket.wifiUserId) : null;

              return (
                <motion.div
                  key={ticket.id}
                  layout
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                >
                  <GlassPanel className="hover:bg-white/[0.03] transition-colors">
                    <div className="flex flex-col md:flex-row md:items-start gap-4">
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2 flex-wrap mb-2">
                          <h3 className="font-semibold text-white" data-testid={`text-ticket-subject-${ticket.id}`}>
                            {ticket.subject}
                          </h3>
                          <Badge 
                            variant="outline" 
                            className={cn("text-xs", statusColors[ticket.status as keyof typeof statusColors])}
                          >
                            {statusLabels[ticket.status as keyof typeof statusLabels]}
                          </Badge>
                          <Badge 
                            variant="outline" 
                            className={cn("text-xs flex items-center gap-1", priorityColors[ticket.priority as keyof typeof priorityColors])}
                          >
                            <PriorityIcon size={12} />
                            {priorityLabels[ticket.priority as keyof typeof priorityLabels]}
                          </Badge>
                        </div>

                        <p className="text-sm text-muted-foreground line-clamp-2 mb-2">
                          {ticket.issueDetails}
                        </p>

                        <div className="flex items-center gap-4 text-xs text-muted-foreground">
                          {wifiUser && (
                            <span className="flex items-center gap-1">
                              <User size={12} />
                              {wifiUser.fullName || wifiUser.phoneNumber}
                            </span>
                          )}
                          <span className="flex items-center gap-1">
                            <Clock size={12} />
                            Created {formatDistanceToNow(new Date(ticket.createdAt!), { addSuffix: true })}
                          </span>
                          {ticket.closedAt && (
                            <span className="flex items-center gap-1">
                              <CheckCircle size={12} />
                              Closed {formatDistanceToNow(new Date(ticket.closedAt), { addSuffix: true })}
                            </span>
                          )}
                        </div>

                        {ticket.resolutionNotes && (
                          <div className="mt-3 p-3 bg-white/5 rounded-lg border border-white/10">
                            <p className="text-xs text-muted-foreground mb-1">Resolution Notes:</p>
                            <p className="text-sm text-white">{ticket.resolutionNotes}</p>
                          </div>
                        )}
                      </div>

                      <div className="flex items-center gap-2">
                        {ticket.status !== "CLOSED" && (
                          <Select 
                            value={ticket.status} 
                            onValueChange={(status) => updateStatusMutation.mutate({ id: ticket.id, status })}
                          >
                            <SelectTrigger className="w-[130px] bg-white/5 border-white/10 text-xs h-8">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="OPEN">Open</SelectItem>
                              <SelectItem value="IN_PROGRESS">In Progress</SelectItem>
                              <SelectItem value="RESOLVED">Resolved</SelectItem>
                            </SelectContent>
                          </Select>
                        )}

                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" size="icon" data-testid={`button-ticket-actions-${ticket.id}`}>
                              <MoreVertical size={18} />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end" className="w-48">
                            <DropdownMenuItem onClick={() => handleOpenDialog(ticket)}>
                              <MessageSquare size={16} className="mr-2" />
                              Edit Ticket
                            </DropdownMenuItem>
                            {ticket.status !== "CLOSED" && (
                              <>
                                <DropdownMenuSeparator />
                                <DropdownMenuItem 
                                  onClick={() => handleOpenCloseDialog(ticket)}
                                  className="text-emerald-400"
                                >
                                  <CheckCircle size={16} className="mr-2" />
                                  Close Ticket
                                </DropdownMenuItem>
                              </>
                            )}
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </div>
                    </div>
                  </GlassPanel>
                </motion.div>
              );
            })}
          </AnimatePresence>
        ) : (
          <GlassPanel className="text-center py-12">
            <Ticket size={48} className="mx-auto mb-4 text-muted-foreground opacity-50" />
            <p className="text-muted-foreground mb-4">
              {searchQuery || filterStatus !== "all" || filterPriority !== "all"
                ? "No tickets match your search criteria"
                : "No support tickets yet"}
            </p>
            {!searchQuery && filterStatus === "all" && filterPriority === "all" && (
              <Button
                className="gradient-btn"
                onClick={() => handleOpenDialog()}
                data-testid="button-create-first-ticket"
              >
                Create Your First Ticket
              </Button>
            )}
          </GlassPanel>
        )}
      </div>

      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="glass-panel border-white/10 max-w-lg">
          <DialogHeader>
            <DialogTitle className="text-xl font-bold text-white">
              {editingTicket ? "Edit Ticket" : "Create New Ticket"}
            </DialogTitle>
          </DialogHeader>

          <form onSubmit={handleSubmit} className="space-y-4">
            <GlassInput
              label="Subject"
              placeholder="Brief description of the issue"
              value={formData.subject}
              onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
              required
              data-testid="input-ticket-subject"
            />

            <GlassTextarea
              label="Issue Details"
              placeholder="Describe the issue in detail..."
              value={formData.issueDetails || ""}
              onChange={(e) => setFormData({ ...formData, issueDetails: e.target.value })}
              required
              data-testid="input-ticket-details"
            />

            <div className="space-y-2">
              <label className="text-sm font-medium uppercase tracking-wider text-muted-foreground">
                Priority
              </label>
              <div className="flex gap-2">
                {(["LOW", "MEDIUM", "HIGH", "URGENT"] as const).map((priority) => (
                  <button
                    key={priority}
                    type="button"
                    onClick={() => setFormData({ ...formData, priority })}
                    className={cn(
                      "flex-1 px-3 py-2 rounded-lg text-sm font-medium transition-all",
                      formData.priority === priority
                        ? priorityColors[priority]
                        : "bg-white/5 text-muted-foreground border border-white/10 hover:bg-white/10"
                    )}
                    data-testid={`button-priority-${priority}`}
                  >
                    {priorityLabels[priority]}
                  </button>
                ))}
              </div>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium uppercase tracking-wider text-muted-foreground">
                Related Customer (Optional)
              </label>
              <Select 
                value={formData.wifiUserId || ""} 
                onValueChange={(value) => setFormData({ ...formData, wifiUserId: value || undefined })}
              >
                <SelectTrigger className="bg-white/5 border-white/10" data-testid="select-ticket-user">
                  <SelectValue placeholder="Select a customer (optional)" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">None</SelectItem>
                  {wifiUsers?.map((user) => (
                    <SelectItem key={user.id} value={user.id}>
                      {user.fullName || user.phoneNumber}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="ghost"
                onClick={handleCloseDialog}
                className="flex-1"
                data-testid="button-cancel-ticket"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                className="flex-1 gradient-btn"
                disabled={saveMutation.isPending}
                data-testid="button-save-ticket"
              >
                {saveMutation.isPending ? "Saving..." : editingTicket ? "Update Ticket" : "Create Ticket"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      <Dialog open={isCloseDialogOpen} onOpenChange={setIsCloseDialogOpen}>
        <DialogContent className="glass-panel border-white/10 max-w-lg">
          <DialogHeader>
            <DialogTitle className="text-xl font-bold text-white">
              Close Ticket
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <p className="text-muted-foreground">
              You are about to close ticket: <strong className="text-white">{closingTicket?.subject}</strong>
            </p>

            <GlassTextarea
              label="Resolution Notes"
              placeholder="Describe how the issue was resolved..."
              value={resolutionNotes}
              onChange={(e) => setResolutionNotes(e.target.value)}
              data-testid="input-resolution-notes"
            />

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="ghost"
                onClick={() => setIsCloseDialogOpen(false)}
                className="flex-1"
              >
                Cancel
              </Button>
              <Button
                onClick={() => closingTicket && closeMutation.mutate({ 
                  id: closingTicket.id, 
                  resolutionNotes 
                })}
                className="flex-1 gradient-btn"
                disabled={closeMutation.isPending}
                data-testid="button-confirm-close"
              >
                {closeMutation.isPending ? "Closing..." : "Close Ticket"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, boolean, timestamp, jsonb, inet } from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Account types for WiFi users
export const AccountType = {
  HOTSPOT: "HOTSPOT",
  PPPOE: "PPPOE",
  STATIC: "STATIC",
} as const;
export type AccountTypeValue = typeof AccountType[keyof typeof AccountType];

// Ticket status
export const TicketStatus = {
  OPEN: "OPEN",
  IN_PROGRESS: "IN_PROGRESS",
  RESOLVED: "RESOLVED",
  CLOSED: "CLOSED",
} as const;
export type TicketStatusValue = typeof TicketStatus[keyof typeof TicketStatus];

// Ticket priority
export const TicketPriority = {
  LOW: "LOW",
  MEDIUM: "MEDIUM",
  HIGH: "HIGH",
  URGENT: "URGENT",
} as const;
export type TicketPriorityValue = typeof TicketPriority[keyof typeof TicketPriority];

// SaaS billing status for tenant
export const SaaSBillingStatus = {
  ACTIVE: "ACTIVE",
  TRIAL: "TRIAL",
  SUSPENDED: "SUSPENDED",
  BLOCKED: "BLOCKED",
} as const;
export type SaaSBillingStatusValue = typeof SaaSBillingStatus[keyof typeof SaaSBillingStatus];

// Tenant subscription tier
export const TenantTier = {
  TRIAL: "TRIAL",
  TIER_1: "TIER_1",
  TIER_2: "TIER_2",
} as const;
export type TenantTierValue = typeof TenantTier[keyof typeof TenantTier];

// User roles for RBAC
export const UserRole = {
  SUPERADMIN: "superadmin",
  ADMIN: "admin",
  TECH: "tech",
} as const;
export type UserRoleValue = typeof UserRole[keyof typeof UserRole];

// WiFi User status
export const WifiUserStatus = {
  ACTIVE: "ACTIVE",
  SUSPENDED: "SUSPENDED",
  EXPIRED: "EXPIRED",
} as const;
export type WifiUserStatusValue = typeof WifiUserStatus[keyof typeof WifiUserStatus];

// Reconciliation status for transactions
export const ReconciliationStatus = {
  MATCHED: "MATCHED",
  UNMATCHED: "UNMATCHED",
  MANUAL_REVIEW: "MANUAL_REVIEW",
  PENDING: "PENDING",
} as const;
export type ReconciliationStatusValue = typeof ReconciliationStatus[keyof typeof ReconciliationStatus];

// Tenant - Multi-tenant SaaS organization
export const tenants = pgTable("tenants", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  subdomain: text("subdomain").notNull().unique(),
  email: text("email"),
  phone: text("phone"),
  brandingConfig: jsonb("branding_config").$type<{
    logo?: string;
    primaryColor?: string;
    secondaryColor?: string;
  }>().default({}),
  mpesaShortcode: text("mpesa_shortcode"),
  mpesaPasskey: text("mpesa_passkey"),
  mpesaConsumerKey: text("mpesa_consumer_key"),
  mpesaConsumerSecret: text("mpesa_consumer_secret"),
  tier: text("tier").default("TRIAL"),
  trialExpiresAt: timestamp("trial_expires_at"),
  subscriptionExpiresAt: timestamp("subscription_expires_at"),
  monthlyRevenue: integer("monthly_revenue").default(0),
  totalUsers: integer("total_users").default(0),
  saasBillingStatus: text("saas_billing_status").default("TRIAL"),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

export const tenantsRelations = relations(tenants, ({ many }) => ({
  hotspots: many(hotspots),
  plans: many(plans),
  transactions: many(transactions),
  walledGardens: many(walledGardens),
  wifiUsers: many(wifiUsers),
  tickets: many(tickets),
}));

// Hotspot - NAS device locations
export const hotspots = pgTable("hotspots", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tenantId: varchar("tenant_id").notNull().references(() => tenants.id),
  nasIp: text("nas_ip").notNull(),
  secret: text("secret").notNull(),
  locationName: text("location_name").notNull(),
  description: text("description"),
  routerApiIp: text("router_api_ip"),
  routerApiUser: text("router_api_user"),
  routerApiPass: text("router_api_pass"),
  routerApiPort: integer("router_api_port").default(8728),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

export const hotspotsRelations = relations(hotspots, ({ one }) => ({
  tenant: one(tenants, {
    fields: [hotspots.tenantId],
    references: [tenants.id],
  }),
}));

// Plan - WiFi subscription plans
export const plans = pgTable("plans", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tenantId: varchar("tenant_id").notNull().references(() => tenants.id),
  name: text("name").notNull(),
  description: text("description"),
  price: integer("price").notNull(), // In smallest currency unit (cents/shillings)
  durationSeconds: integer("duration_seconds").notNull(),
  uploadLimit: text("upload_limit"), // e.g., "2M" for MikroTik
  downloadLimit: text("download_limit"), // e.g., "5M" for MikroTik
  burstRateUp: text("burst_rate_up"), // e.g., "4M" burst upload
  burstRateDown: text("burst_rate_down"), // e.g., "10M" burst download
  burstThreshold: text("burst_threshold"), // e.g., "1M"
  burstTime: integer("burst_time"), // in seconds
  simultaneousUse: integer("simultaneous_use").default(1),
  isActive: boolean("is_active").default(true),
  sortOrder: integer("sort_order").default(0),
  createdAt: timestamp("created_at").defaultNow(),
});

export const plansRelations = relations(plans, ({ one, many }) => ({
  tenant: one(tenants, {
    fields: [plans.tenantId],
    references: [tenants.id],
  }),
  transactions: many(transactions),
}));

// Transaction - Payment records
export const transactions = pgTable("transactions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tenantId: varchar("tenant_id").notNull().references(() => tenants.id),
  planId: varchar("plan_id").references(() => plans.id),
  wifiUserId: varchar("wifi_user_id"),
  userPhone: text("user_phone").notNull(),
  amount: integer("amount").notNull(),
  mpesaReceiptNumber: text("mpesa_receipt_number"),
  checkoutRequestId: text("checkout_request_id"),
  merchantRequestId: text("merchant_request_id"),
  status: text("status").notNull().default("PENDING"), // PENDING, COMPLETED, FAILED
  reconciliationStatus: text("reconciliation_status").default("PENDING"), // MATCHED, UNMATCHED, MANUAL_REVIEW, PENDING
  statusDescription: text("status_description"),
  macAddress: text("mac_address"),
  nasIp: text("nas_ip"),
  radiusSessionId: text("radius_session_id"),
  expiresAt: timestamp("expires_at"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const transactionsRelations = relations(transactions, ({ one }) => ({
  tenant: one(tenants, {
    fields: [transactions.tenantId],
    references: [tenants.id],
  }),
  plan: one(plans, {
    fields: [transactions.planId],
    references: [plans.id],
  }),
}));

// Walled Garden - Allowed domains before authentication
export const walledGardens = pgTable("walled_gardens", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tenantId: varchar("tenant_id").notNull().references(() => tenants.id),
  domain: text("domain").notNull(),
  description: text("description"),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

export const walledGardensRelations = relations(walledGardens, ({ one }) => ({
  tenant: one(tenants, {
    fields: [walledGardens.tenantId],
    references: [tenants.id],
  }),
}));

// Users - Admin users for dashboard access
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tenantId: varchar("tenant_id").references(() => tenants.id),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  email: text("email"),
  role: text("role").notNull().default("admin"), // superadmin, admin, tech
  isActive: boolean("is_active").default(true),
  resetToken: text("reset_token"),
  resetTokenExpiry: timestamp("reset_token_expiry"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const usersRelations = relations(users, ({ one }) => ({
  tenant: one(tenants, {
    fields: [users.tenantId],
    references: [tenants.id],
  }),
}));

// WifiUser - End customers who use the WiFi service
export const wifiUsers = pgTable("wifi_users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tenantId: varchar("tenant_id").notNull().references(() => tenants.id),
  phoneNumber: text("phone_number").notNull(),
  email: text("email"),
  fullName: text("full_name"),
  accountType: text("account_type").notNull().default("HOTSPOT"), // HOTSPOT, PPPOE, STATIC
  currentPlanId: varchar("current_plan_id").references(() => plans.id),
  currentHotspotId: varchar("current_hotspot_id").references(() => hotspots.id),
  expiryTime: timestamp("expiry_time"),
  macAddress: text("mac_address"),
  ipAddress: text("ip_address"),
  username: text("username"),
  password: text("password"),
  status: text("status").notNull().default("ACTIVE"), // ACTIVE, SUSPENDED, EXPIRED
  notes: text("notes"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const wifiUsersRelations = relations(wifiUsers, ({ one, many }) => ({
  tenant: one(tenants, {
    fields: [wifiUsers.tenantId],
    references: [tenants.id],
  }),
  currentPlan: one(plans, {
    fields: [wifiUsers.currentPlanId],
    references: [plans.id],
  }),
  currentHotspot: one(hotspots, {
    fields: [wifiUsers.currentHotspotId],
    references: [hotspots.id],
  }),
  tickets: many(tickets),
}));

// Ticket - Support tickets for customer issues
export const tickets = pgTable("tickets", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tenantId: varchar("tenant_id").notNull().references(() => tenants.id),
  wifiUserId: varchar("wifi_user_id").references(() => wifiUsers.id),
  subject: text("subject").notNull(),
  issueDetails: text("issue_details").notNull(),
  status: text("status").notNull().default("OPEN"), // OPEN, IN_PROGRESS, RESOLVED, CLOSED
  priority: text("priority").notNull().default("MEDIUM"), // LOW, MEDIUM, HIGH, URGENT
  resolutionNotes: text("resolution_notes"),
  assignedTo: varchar("assigned_to").references(() => users.id),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
  closedAt: timestamp("closed_at"),
});

export const ticketsRelations = relations(tickets, ({ one }) => ({
  tenant: one(tenants, {
    fields: [tickets.tenantId],
    references: [tenants.id],
  }),
  wifiUser: one(wifiUsers, {
    fields: [tickets.wifiUserId],
    references: [wifiUsers.id],
  }),
  assignee: one(users, {
    fields: [tickets.assignedTo],
    references: [users.id],
  }),
}));

// Insert Schemas
export const insertTenantSchema = createInsertSchema(tenants).omit({
  id: true,
  createdAt: true,
});

export const insertHotspotSchema = createInsertSchema(hotspots).omit({
  id: true,
  createdAt: true,
});

export const insertPlanSchema = createInsertSchema(plans).omit({
  id: true,
  createdAt: true,
});

export const insertTransactionSchema = createInsertSchema(transactions).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertWalledGardenSchema = createInsertSchema(walledGardens).omit({
  id: true,
  createdAt: true,
});

export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
});

export const insertWifiUserSchema = createInsertSchema(wifiUsers).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertTicketSchema = createInsertSchema(tickets).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  closedAt: true,
});

// Types
export type Tenant = typeof tenants.$inferSelect;
export type InsertTenant = z.infer<typeof insertTenantSchema>;

export type Hotspot = typeof hotspots.$inferSelect;
export type InsertHotspot = z.infer<typeof insertHotspotSchema>;

export type Plan = typeof plans.$inferSelect;
export type InsertPlan = z.infer<typeof insertPlanSchema>;

export type Transaction = typeof transactions.$inferSelect;
export type InsertTransaction = z.infer<typeof insertTransactionSchema>;

export type WalledGarden = typeof walledGardens.$inferSelect;
export type InsertWalledGarden = z.infer<typeof insertWalledGardenSchema>;

export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;

export type WifiUser = typeof wifiUsers.$inferSelect;
export type InsertWifiUser = z.infer<typeof insertWifiUserSchema>;

export type Ticket = typeof tickets.$inferSelect;
export type InsertTicket = z.infer<typeof insertTicketSchema>;

// Transaction status enum for type safety
export const TransactionStatus = {
  PENDING: "PENDING",
  COMPLETED: "COMPLETED",
  FAILED: "FAILED",
} as const;

export type TransactionStatusType = typeof TransactionStatus[keyof typeof TransactionStatus];

// Job status for background queue
export const JobStatus = {
  PENDING: "PENDING",
  PROCESSING: "PROCESSING",
  COMPLETED: "COMPLETED",
  FAILED: "FAILED",
  RETRY: "RETRY",
} as const;
export type JobStatusType = typeof JobStatus[keyof typeof JobStatus];

// Job types
export const JobType = {
  PAYMENT_STATUS_CHECK: "PAYMENT_STATUS_CHECK",
  USER_EXPIRY_CHECK: "USER_EXPIRY_CHECK",
  RECONCILIATION: "RECONCILIATION",
  SMS_NOTIFICATION: "SMS_NOTIFICATION",
  EMAIL_NOTIFICATION: "EMAIL_NOTIFICATION",
} as const;
export type JobTypeValue = typeof JobType[keyof typeof JobType];

// Background Jobs - Database-backed job queue for resilience
export const jobs = pgTable("jobs", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tenantId: varchar("tenant_id").references(() => tenants.id),
  type: text("type").notNull(), // PAYMENT_STATUS_CHECK, USER_EXPIRY_CHECK, etc.
  payload: jsonb("payload").$type<Record<string, unknown>>().notNull(),
  status: text("status").notNull().default("PENDING"), // PENDING, PROCESSING, COMPLETED, FAILED, RETRY
  priority: integer("priority").default(0), // Higher = more urgent
  attempts: integer("attempts").default(0),
  maxAttempts: integer("max_attempts").default(3),
  lastError: text("last_error"),
  scheduledFor: timestamp("scheduled_for").defaultNow(),
  startedAt: timestamp("started_at"),
  completedAt: timestamp("completed_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const jobsRelations = relations(jobs, ({ one }) => ({
  tenant: one(tenants, {
    fields: [jobs.tenantId],
    references: [tenants.id],
  }),
}));

export const insertJobSchema = createInsertSchema(jobs).omit({
  id: true,
  attempts: true,
  startedAt: true,
  completedAt: true,
  createdAt: true,
});

export type Job = typeof jobs.$inferSelect;
export type InsertJob = z.infer<typeof insertJobSchema>;

