@tailwind base;
@tailwind components;
@tailwind utilities;

/* MnetiFi Glassmorphism Dark Theme - Vaultic Inspired */
:root {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);
  --opaque-button-border-intensity: 9;
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  /* Deep Navy Base */
  --background: 222 47% 11%;
  --foreground: 210 40% 98%;

  /* Glassmorphism Borders */
  --border: 217 33% 17%;

  /* Glass Card Surface */
  --card: 222 47% 13%;
  --card-foreground: 210 40% 98%;
  --card-border: 217 33% 20%;

  /* Sidebar - Glass Panel */
  --sidebar: 222 47% 12%;
  --sidebar-foreground: 210 40% 98%;
  --sidebar-border: 217 33% 18%;
  --sidebar-primary: 189 94% 43%;
  --sidebar-primary-foreground: 210 40% 98%;
  --sidebar-accent: 222 30% 18%;
  --sidebar-accent-foreground: 210 40% 98%;
  --sidebar-ring: 189 94% 43%;

  /* Popover - Glass Surface */
  --popover: 222 47% 14%;
  --popover-foreground: 210 40% 98%;
  --popover-border: 217 33% 22%;

  /* Primary - Cyan Accent */
  --primary: 189 94% 43%;
  --primary-foreground: 222 47% 11%;

  /* Secondary - Glass Muted */
  --secondary: 217 33% 17%;
  --secondary-foreground: 210 40% 98%;

  /* Muted Text */
  --muted: 215 20% 20%;
  --muted-foreground: 215 20% 65%;

  /* Accent - Subtle Glass */
  --accent: 217 33% 18%;
  --accent-foreground: 210 40% 98%;

  /* Destructive - Magenta */
  --destructive: 330 85% 60%;
  --destructive-foreground: 210 40% 98%;

  /* Input - Recessed Style */
  --input: 222 47% 8%;

  /* Ring - Cyan Focus */
  --ring: 189 94% 43%;

  /* Charts */
  --chart-1: 189 94% 50%;
  --chart-2: 263 70% 50%;
  --chart-3: 330 85% 60%;
  --chart-4: 217 91% 60%;
  --chart-5: 173 58% 39%;

  --font-sans: Inter, Plus Jakarta Sans, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem;

  /* Glass Shadows */
  --shadow-2xs: 0px 2px 0px 0px hsl(222 47% 5% / 0.20);
  --shadow-xs: 0px 2px 0px 0px hsl(222 47% 5% / 0.25);
  --shadow-sm: 0px 2px 0px 0px hsl(222 47% 5% / 0.25), 0px 1px 2px -1px hsl(222 47% 5% / 0.30);
  --shadow: 0px 2px 0px 0px hsl(222 47% 5% / 0.25), 0px 1px 2px -1px hsl(222 47% 5% / 0.30);
  --shadow-md: 0px 2px 0px 0px hsl(222 47% 5% / 0.30), 0px 2px 4px -1px hsl(222 47% 5% / 0.35);
  --shadow-lg: 0px 2px 0px 0px hsl(222 47% 5% / 0.35), 0px 4px 6px -1px hsl(222 47% 5% / 0.40);
  --shadow-xl: 0px 2px 0px 0px hsl(222 47% 5% / 0.40), 0px 8px 10px -1px hsl(222 47% 5% / 0.45);
  --shadow-2xl: 0px 2px 0px 0px hsl(222 47% 5% / 0.50);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Border Color Fallbacks */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --primary-border: hsl(var(--primary));
  --secondary-border: hsl(var(--secondary));
  --muted-border: hsl(var(--muted));
  --accent-border: hsl(var(--accent));
  --destructive-border: hsl(var(--destructive));
}

/* Force dark mode always for MnetiFi */
.dark,
html {
  color-scheme: dark;
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    @apply dark;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    background-color: #0f172a;
  }
}

@layer components {
  /* Mesh Gradient Background */
  .mesh-bg {
    position: fixed;
    inset: 0;
    z-index: -1;
    background-color: #0f172a;
    overflow: hidden;
  }

  .mesh-bg::before {
    content: "";
    position: absolute;
    width: 150%;
    height: 150%;
    top: -25%;
    left: -25%;
    background: 
      radial-gradient(ellipse at 20% 20%, rgba(124, 58, 237, 0.35) 0%, transparent 50%),
      radial-gradient(ellipse at 80% 20%, rgba(6, 182, 212, 0.3) 0%, transparent 50%),
      radial-gradient(ellipse at 60% 80%, rgba(236, 72, 153, 0.3) 0%, transparent 50%),
      radial-gradient(ellipse at 10% 70%, rgba(59, 130, 246, 0.25) 0%, transparent 45%);
    animation: mesh-drift 45s ease-in-out infinite;
  }

  @keyframes mesh-drift {
    0%, 100% {
      transform: translate(0, 0) rotate(0deg);
    }
    25% {
      transform: translate(3%, 2%) rotate(1deg);
    }
    50% {
      transform: translate(-2%, 4%) rotate(-1deg);
    }
    75% {
      transform: translate(4%, -2%) rotate(0.5deg);
    }
  }

  /* Glass Panel */
  .glass-panel {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(16px) saturate(180%);
    -webkit-backdrop-filter: blur(16px) saturate(180%);
    border-radius: 24px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-top-color: rgba(255, 255, 255, 0.2);
    border-left-color: rgba(255, 255, 255, 0.15);
    box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
  }

  /* Glass Panel Hover */
  .glass-panel-hover {
    transition: all 0.3s ease;
  }

  .glass-panel-hover:hover {
    background: rgba(255, 255, 255, 0.08);
    transform: translateY(-4px);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.15);
  }

  /* Glass Input - Recessed Style */
  .glass-input {
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    transition: all 0.2s ease;
  }

  .glass-input:focus {
    border-color: rgba(255, 255, 255, 0.3);
    box-shadow: 0 0 0 2px rgba(6, 182, 212, 0.2);
    outline: none;
  }

  .glass-input::placeholder {
    color: rgba(255, 255, 255, 0.3);
  }

  /* Gradient Text */
  .gradient-text {
    background: linear-gradient(135deg, #06b6d4 0%, #3b82f6 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .gradient-text-accent {
    background: linear-gradient(135deg, #7c3aed 0%, #ec4899 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Gradient Button */
  .gradient-btn {
    background: linear-gradient(135deg, #06b6d4 0%, #3b82f6 100%);
    border: none;
    border-radius: 16px;
    color: white;
    font-weight: 600;
    transition: all 0.2s ease;
    position: relative;
    overflow: hidden;
  }

  .gradient-btn::before {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(135deg, rgba(255,255,255,0.2) 0%, transparent 50%);
    opacity: 0;
    transition: opacity 0.2s ease;
  }

  .gradient-btn:hover::before {
    opacity: 1;
  }

  .gradient-btn:active {
    transform: scale(0.98);
  }

  /* M-Pesa Button */
  .mpesa-btn {
    background: linear-gradient(135deg, #4BB617 0%, #5fd423 100%);
    border: none;
    border-radius: 16px;
    color: white;
    font-weight: 600;
    transition: all 0.2s ease;
    box-shadow: 0 4px 20px rgba(75, 182, 23, 0.3);
  }

  .mpesa-btn:hover {
    box-shadow: 0 6px 30px rgba(75, 182, 23, 0.4);
    transform: translateY(-2px);
  }

  .mpesa-btn:active {
    transform: scale(0.98) translateY(0);
  }

  /* Status Indicators */
  .status-pending {
    animation: pulse-purple 2s ease-in-out infinite;
  }

  @keyframes pulse-purple {
    0%, 100% {
      box-shadow: 0 0 10px rgba(124, 58, 237, 0.4);
    }
    50% {
      box-shadow: 0 0 20px rgba(124, 58, 237, 0.7);
    }
  }

  .status-completed {
    box-shadow: 0 0 15px rgba(6, 182, 212, 0.4);
  }

  .status-failed {
    box-shadow: 0 0 15px rgba(236, 72, 153, 0.4);
  }

  /* Scrollbar Styling */
  ::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  ::-webkit-scrollbar-track {
    background: rgba(255, 255, 255, 0.05);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, 0.1);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: rgba(255, 255, 255, 0.2);
  }

  /* Mobile Fallback for Glass */
  @supports not (backdrop-filter: blur(16px)) {
    .glass-panel {
      background: rgba(15, 23, 42, 0.95);
    }
  }
}

@layer utilities {
  /* Hide ugly search cancel button in Chrome */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after {
    inset: -1px;
  }

  /* Text hierarchy utilities */
  .text-primary-content {
    color: #FFFFFF;
  }

  .text-secondary-content {
    color: #94a3b8;
  }

  .text-tertiary-content {
    color: rgba(255, 255, 255, 0.5);
  }
}

