import { useEffect } from "react";
import { useLocation, Link } from "wouter";
import { SidebarProvider, SidebarTrigger } from "@/components/ui/sidebar";
import { MeshBackground } from "@/components/mesh-background";
import { useRequireAuth } from "@/hooks/use-session";
import { Loader2, Building2, LayoutDashboard, Users, LogOut, Shield, UserCog } from "lucide-react";
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupLabel,
  SidebarGroupContent,
  SidebarMenu,
  SidebarMenuItem,
  SidebarMenuButton,
  SidebarFooter,
  SidebarHeader,
} from "@/components/ui/sidebar";
import { Button } from "@/components/ui/button";

interface SuperAdminLayoutProps {
  children: React.ReactNode;
}

const menuItems = [
  { title: "Dashboard", url: "/superadmin", icon: LayoutDashboard },
  { title: "Tenants", url: "/superadmin/tenants", icon: Building2 },
  { title: "Users", url: "/superadmin/users", icon: UserCog },
];

function SuperAdminSidebar({ onLogout }: { onLogout: () => void }) {
  const [location] = useLocation();

  return (
    <Sidebar>
      <SidebarHeader className="p-4 border-b border-white/10">
        <div className="flex items-center gap-3">
          <div className="p-2 rounded-lg bg-gradient-to-br from-cyan-500/20 to-purple-500/20">
            <Shield size={20} className="text-cyan-400" />
          </div>
          <div>
            <h2 className="font-bold text-white">Super Admin</h2>
            <p className="text-xs text-muted-foreground">Platform Control</p>
          </div>
        </div>
      </SidebarHeader>

      <SidebarContent>
        <SidebarGroup>
          <SidebarGroupLabel>Platform</SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              {menuItems.map((item) => (
                <SidebarMenuItem key={item.title}>
                  <SidebarMenuButton
                    asChild
                    data-active={location === item.url}
                    className="data-[active=true]:bg-sidebar-accent"
                  >
                    <Link href={item.url} data-testid={`nav-${item.title.toLowerCase()}`}>
                      <item.icon size={18} />
                      <span>{item.title}</span>
                    </Link>
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>

      <SidebarFooter className="p-4 border-t border-white/10">
        <Link href="/dashboard">
          <Button variant="outline" size="sm" className="w-full mb-2" data-testid="button-switch-to-tenant">
            <Building2 size={16} className="mr-2" />
            Tenant Dashboard
          </Button>
        </Link>
        <Button 
          variant="ghost" 
          size="sm" 
          className="w-full justify-start text-muted-foreground" 
          onClick={onLogout}
          data-testid="button-logout"
        >
          <LogOut size={16} className="mr-2" />
          Logout
        </Button>
      </SidebarFooter>
    </Sidebar>
  );
}

export function SuperAdminLayout({ children }: SuperAdminLayoutProps) {
  const { user, isLoading, logout } = useRequireAuth();
  const [, setLocation] = useLocation();

  const style = {
    "--sidebar-width": "16rem",
    "--sidebar-width-icon": "3rem",
  } as React.CSSProperties;

  useEffect(() => {
    if (!isLoading && !user) {
      setLocation("/superadmin/login");
    } else if (!isLoading && user && user.role !== "superadmin") {
      setLocation("/superadmin/login");
    }
  }, [user, isLoading, setLocation]);

  if (isLoading) {
    return (
      <>
        <MeshBackground />
        <div className="min-h-screen flex items-center justify-center">
          <Loader2 className="w-8 h-8 animate-spin text-cyan-400" />
        </div>
      </>
    );
  }

  if (!user || user.role !== "superadmin") {
    return null;
  }

  return (
    <>
      <MeshBackground />
      <SidebarProvider style={style}>
        <div className="flex h-screen w-full relative z-10">
          <SuperAdminSidebar onLogout={logout} />
          <div className="flex flex-col flex-1 overflow-hidden">
            <header className="flex items-center gap-4 p-4 border-b border-white/10 bg-background/50 backdrop-blur-sm">
              <SidebarTrigger data-testid="button-sidebar-toggle" />
              <div className="flex-1" />
              <div className="flex items-center gap-2">
                <Shield size={16} className="text-cyan-400" />
                <span className="text-sm text-muted-foreground">
                  {user.username}
                </span>
              </div>
            </header>
            <main className="flex-1 overflow-auto">
              {children}
            </main>
          </div>
        </div>
      </SidebarProvider>
    </>
  );
}

client/src/pages/superadmin-dashboard.ts.content:
import { useQuery } from "@tanstack/react-query";
import { motion } from "framer-motion";
import { Link } from "wouter";
import {
  Building2,
  Users,
  DollarSign,
  TrendingUp,
  Play,
  Pause,
  AlertTriangle,
  Clock,
  CheckCircle,
  Calendar,
  ArrowRight,
} from "lucide-react";
import { MetricCard, MetricCardSkeleton } from "@/components/metric-card";
import { GlassPanel } from "@/components/glass-panel";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { format } from "date-fns";

interface PlatformAnalytics {
  totalTenants: number;
  activeTenants: number;
  trialTenants: number;
  suspendedTenants: number;
  totalUsers: number;
  totalRevenue: number;
  revenueThisMonth: number;
  revenueLastMonth: number;
  transactionsToday: number;
  newTenantsThisMonth: number;
}

interface TenantWithStats {
  id: string;
  name: string;
  subdomain: string;
  tier: string;
  isActive: boolean;
  saasBillingStatus: string;
  trialExpiresAt: string | null;
  subscriptionExpiresAt: string | null;
  createdAt: string;
  userCount: number;
  transactionCount: number;
  revenueThisMonth: number;
}

export default function SuperAdminDashboard() {
  const { data: analytics, isLoading: analyticsLoading } = useQuery<PlatformAnalytics>({
    queryKey: ["/api/superadmin/analytics"],
  });

  const { data: tenants, isLoading: tenantsLoading } = useQuery<TenantWithStats[]>({
    queryKey: ["/api/superadmin/tenants"],
  });

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat("en-KE", {
      style: "currency",
      currency: "KES",
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const getRevenueGrowth = () => {
    if (!analytics?.revenueLastMonth) return 0;
    return Math.round(
      ((analytics.revenueThisMonth - analytics.revenueLastMonth) / analytics.revenueLastMonth) * 100
    );
  };

  const getStatusBadge = (status: string) => {
    const statusConfig: Record<string, { bg: string; text: string; label: string }> = {
      ACTIVE: { bg: "bg-cyan-500/20", text: "text-cyan-400", label: "Active" },
      TRIAL: { bg: "bg-purple-500/20", text: "text-purple-400", label: "Trial" },
      SUSPENDED: { bg: "bg-amber-500/20", text: "text-amber-400", label: "Suspended" },
      BLOCKED: { bg: "bg-red-500/20", text: "text-red-400", label: "Blocked" },
      PENDING: { bg: "bg-slate-500/20", text: "text-slate-400", label: "Pending" },
    };
    const config = statusConfig[status] || statusConfig.PENDING;
    return (
      <Badge variant="outline" className={`${config.bg} ${config.text} border-0`}>
        {config.label}
      </Badge>
    );
  };

  const getTierBadge = (tier: string) => {
    const tierConfig: Record<string, { bg: string; text: string }> = {
      TRIAL: { bg: "bg-purple-500/20", text: "text-purple-400" },
      TIER_1: { bg: "bg-cyan-500/20", text: "text-cyan-400" },
      TIER_2: { bg: "bg-pink-500/20", text: "text-pink-400" },
    };
    const config = tierConfig[tier] || tierConfig.TRIAL;
    return (
      <Badge variant="outline" className={`${config.bg} ${config.text} border-0`}>
        {tier.replace("_", " ")}
      </Badge>
    );
  };

  return (
    <div className="p-6 space-y-6" data-testid="superadmin-dashboard-page">
      <div className="flex items-center justify-between gap-4 flex-wrap">
        <div>
          <h1 className="text-2xl font-bold text-white">Platform Overview</h1>
          <p className="text-muted-foreground">
            Monitor and manage all tenants across the platform.
          </p>
        </div>
        <Link href="/superadmin/tenants">
          <Button data-testid="button-manage-tenants">
            Manage Tenants
            <ArrowRight size={16} className="ml-2" />
          </Button>
        </Link>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-5 gap-4">
        {analyticsLoading ? (
          <>
            <MetricCardSkeleton />
            <MetricCardSkeleton />
            <MetricCardSkeleton />
            <MetricCardSkeleton />
            <MetricCardSkeleton />
          </>
        ) : (
          <>
            <MetricCard
              title="Total Tenants"
              value={analytics?.totalTenants || 0}
              icon={<Building2 size={24} />}
              gradient="primary"
            />
            <MetricCard
              title="Platform Revenue"
              value={formatCurrency(analytics?.totalRevenue || 0)}
              icon={<DollarSign size={24} />}
              trend={getRevenueGrowth() !== 0 ? { value: getRevenueGrowth(), label: "vs last month" } : undefined}
              gradient="mpesa"
            />
            <MetricCard
              title="This Month"
              value={formatCurrency(analytics?.revenueThisMonth || 0)}
              icon={<TrendingUp size={24} />}
            />
            <MetricCard
              title="Total Users"
              value={analytics?.totalUsers || 0}
              icon={<Users size={24} />}
              gradient="accent"
            />
            <MetricCard
              title="Today's Transactions"
              value={analytics?.transactionsToday || 0}
              icon={<Calendar size={24} />}
            />
          </>
        )}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <motion.div
          className="flex items-center gap-4 p-4 rounded-xl bg-cyan-500/10 border border-cyan-500/20"
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          data-testid="status-active-tenants"
        >
          <div className="p-3 rounded-full bg-cyan-500/20">
            <CheckCircle size={24} className="text-cyan-400" />
          </div>
          <div>
            <p className="text-sm text-muted-foreground">Active</p>
            <p className="text-2xl font-bold text-white">
              {analytics?.activeTenants || 0}
            </p>
          </div>
        </motion.div>

        <motion.div
          className="flex items-center gap-4 p-4 rounded-xl bg-purple-500/10 border border-purple-500/20"
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          data-testid="status-trial-tenants"
        >
          <div className="p-3 rounded-full bg-purple-500/20">
            <Clock size={24} className="text-purple-400" />
          </div>
          <div>
            <p className="text-sm text-muted-foreground">On Trial</p>
            <p className="text-2xl font-bold text-white">
              {analytics?.trialTenants || 0}
            </p>
          </div>
        </motion.div>

        <motion.div
          className="flex items-center gap-4 p-4 rounded-xl bg-amber-500/10 border border-amber-500/20"
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
          data-testid="status-suspended-tenants"
        >
          <div className="p-3 rounded-full bg-amber-500/20">
            <Pause size={24} className="text-amber-400" />
          </div>
          <div>
            <p className="text-sm text-muted-foreground">Suspended</p>
            <p className="text-2xl font-bold text-white">
              {analytics?.suspendedTenants || 0}
            </p>
          </div>
        </motion.div>

        <motion.div
          className="flex items-center gap-4 p-4 rounded-xl bg-green-500/10 border border-green-500/20"
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          data-testid="status-new-tenants"
        >
          <div className="p-3 rounded-full bg-green-500/20">
            <Play size={24} className="text-green-400" />
          </div>
          <div>
            <p className="text-sm text-muted-foreground">New This Month</p>
            <p className="text-2xl font-bold text-white">
              {analytics?.newTenantsThisMonth || 0}
            </p>
          </div>
        </motion.div>
      </div>

      <GlassPanel size="md">
        <div className="flex items-center justify-between mb-4 gap-4 flex-wrap">
          <h2 className="text-lg font-semibold text-white">Recent Tenants</h2>
          <Link
            href="/superadmin/tenants"
            className="text-sm text-cyan-400 hover:text-cyan-300 transition-colors"
            data-testid="link-view-all-tenants"
          >
            View All
          </Link>
        </div>

        {tenantsLoading ? (
          <div className="space-y-3">
            {[1, 2, 3, 4, 5].map((i) => (
              <div key={i} className="animate-pulse flex items-center gap-4 p-4 rounded-lg bg-white/5">
                <div className="w-10 h-10 rounded-full bg-white/10" />
                <div className="flex-1 space-y-2">
                  <div className="h-4 bg-white/10 rounded w-1/3" />
                  <div className="h-3 bg-white/10 rounded w-1/4" />
                </div>
                <div className="h-6 w-16 bg-white/10 rounded" />
              </div>
            ))}
          </div>
        ) : tenants && tenants.length > 0 ? (
          <div className="space-y-3">
            {tenants.slice(0, 5).map((tenant) => (
              <motion.div
                key={tenant.id}
                initial={{ opacity: 0, x: -10 }}
                animate={{ opacity: 1, x: 0 }}
                className="flex items-center gap-4 p-4 rounded-lg border bg-white/5 border-white/10"
                data-testid={`tenant-row-${tenant.id}`}
              >
                <div className="w-10 h-10 rounded-full bg-gradient-to-br from-cyan-500/20 to-purple-500/20 flex items-center justify-center">
                  <Building2 size={18} className="text-cyan-400" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="font-medium text-white truncate">{tenant.name}</p>
                  <p className="text-xs text-muted-foreground">{tenant.subdomain}</p>
                </div>
                <div className="flex items-center gap-2 flex-wrap justify-end">
                  {getTierBadge(tenant.tier)}
                  {getStatusBadge(tenant.saasBillingStatus)}
                </div>
                <div className="text-right hidden sm:block">
                  <p className="text-sm font-medium text-white">
                    {formatCurrency(tenant.revenueThisMonth)}
                  </p>
                  <p className="text-xs text-muted-foreground">
                    {tenant.userCount} users
                  </p>
                </div>
              </motion.div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8 text-muted-foreground">
            <Building2 size={48} className="mx-auto mb-4 opacity-50" />
            <p>No tenants found</p>
          </div>
        )}
      </GlassPanel>
    </div>
  );
}

client/src/pages/superadmin-forgot-passw.content:
import { useState } from "react";
import { Link } from "wouter";
import { motion } from "framer-motion";
import { Mail, Loader2, ArrowRight, ArrowLeft, Check, KeyRound, ShieldCheck } from "lucide-react";
import { MeshBackground } from "@/components/mesh-background";
import { MnetiFiLogo } from "@/components/mnetifi-logo";
import { GlassPanel } from "@/components/glass-panel";
import { GlassInput } from "@/components/glass-input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";

export default function SuperAdminForgotPasswordPage() {
  const { toast } = useToast();
  const [step, setStep] = useState<'request' | 'sent'>('request');
  const [isLoading, setIsLoading] = useState(false);
  const [email, setEmail] = useState("");

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email.trim()) {
      toast({
        title: "Email required",
        description: "Please enter your email address",
        variant: "destructive",
      });
      return;
    }

    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      toast({
        title: "Invalid email",
        description: "Please enter a valid email address",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const response = await fetch("/api/auth/forgot-password", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();

      if (response.ok) {
        setStep('sent');
        toast({
          title: "Reset link sent",
          description: "Check your email for password reset instructions",
        });
      } else {
        toast({
          title: "Request Failed",
          description: data.error || "Unable to process request",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Request Failed",
        description: "Unable to connect to server",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <MeshBackground />
      <div className="min-h-screen flex items-center justify-center p-4 relative z-10">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="w-full max-w-md"
        >
          <GlassPanel size="lg" className="text-center">
            <div className="mb-8">
              <MnetiFiLogo size="lg" className="mx-auto mb-4" />
              <div className="flex items-center justify-center gap-2 mb-2">
                <Badge variant="outline" className="bg-pink-500/20 text-pink-400 border-0">
                  <ShieldCheck size={12} className="mr-1" />
                  Platform Administration
                </Badge>
              </div>
              <h1 className="text-2xl font-bold text-white mb-2">
                {step === 'request' ? 'Super Admin Password Reset' : 'Check Your Email'}
              </h1>
              <p className="text-muted-foreground">
                {step === 'request' 
                  ? 'Enter your Super Admin email to receive a reset link' 
                  : 'We sent you password reset instructions'}
              </p>
            </div>

            {step === 'request' ? (
              <form onSubmit={handleSubmit} className="space-y-4">
                <GlassInput
                  label="Email Address"
                  type="email"
                  placeholder="Enter your Super Admin email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  icon={<Mail size={16} />}
                  data-testid="input-superadmin-email"
                />

                <Button
                  type="submit"
                  className="w-full bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700"
                  disabled={isLoading}
                  data-testid="button-send-reset"
                >
                  {isLoading ? (
                    <>
                      <Loader2 size={18} className="mr-2 animate-spin" />
                      Sending...
                    </>
                  ) : (
                    <>
                      Send Reset Link
                      <ArrowRight size={18} className="ml-2" />
                    </>
                  )}
                </Button>
              </form>
            ) : (
              <div className="space-y-6">
                <div className="w-16 h-16 mx-auto rounded-full bg-pink-500/20 flex items-center justify-center">
                  <Check size={32} className="text-pink-400" />
                </div>

                <div className="space-y-2">
                  <p className="text-white/80">
                    We sent a password reset link to:
                  </p>
                  <p className="text-pink-400 font-medium">
                    {email}
                  </p>
                </div>

                <div className="p-4 rounded-lg bg-white/5 text-left">
                  <p className="text-sm text-muted-foreground">
                    <KeyRound size={14} className="inline mr-2" />
                    The link will expire in 1 hour. If you don't see the email, check your spam folder.
                  </p>
                </div>

                <Button
                  onClick={() => { setStep('request'); setEmail(''); }}
                  variant="outline"
                  className="w-full border-pink-500/30 text-pink-400 hover:bg-pink-500/10"
                  data-testid="button-try-again"
                >
                  <ArrowLeft size={18} className="mr-2" />
                  Try a different email
                </Button>
              </div>
            )}

            <div className="mt-6 pt-6 border-t border-white/10">
              <p className="text-sm text-muted-foreground">
                Remember your password?{" "}
                <Link 
                  href="/superadmin/login" 
                  className="text-pink-400 hover:text-pink-300 transition-colors font-medium"
                  data-testid="link-superadmin-login"
                >
                  Back to Super Admin login
                </Link>
              </p>
            </div>

            <div className="mt-4 pt-4 border-t border-white/10">
              <p className="text-xs text-muted-foreground">
                ISP Administrator?{" "}
                <Link 
                  href="/forgot-password" 
                  className="text-cyan-400 hover:text-cyan-300 transition-colors font-medium"
                >
                  ISP Password Reset
                </Link>
              </p>
            </div>
          </GlassPanel>
        </motion.div>
      </div>
    </>
  );
}

client/src/pages/superadmin-users.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { motion } from "framer-motion";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { z } from "zod";
import {
  UserPlus,
  Users,
  Shield,
  ShieldCheck,
  Wrench,
  MoreVertical,
  Trash2,
  Power,
  PowerOff,
  Mail,
  Calendar,
} from "lucide-react";
import { GlassPanel } from "@/components/glass-panel";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
  DropdownMenuSeparator,
} from "@/components/ui/dropdown-menu";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { format } from "date-fns";

interface AdminUser {
  id: string;
  username: string;
  email: string | null;
  role: string;
  isActive: boolean;
  tenantId: string | null;
  createdAt: string;
  tenantName?: string;
}

const createUserSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  role: z.enum(["superadmin", "admin", "tech"]),
});

type CreateUserForm = z.infer<typeof createUserSchema>;

export default function SuperAdminUsersPage() {
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const { toast } = useToast();

  const form = useForm<CreateUserForm>({
    resolver: zodResolver(createUserSchema),
    defaultValues: {
      username: "",
      email: "",
      password: "",
      role: "superadmin",
    },
  });

  const { data: users, isLoading } = useQuery<AdminUser[]>({
    queryKey: ["/api/superadmin/users"],
  });

  const createUserMutation = useMutation({
    mutationFn: async (data: CreateUserForm) => {
      return apiRequest("POST", "/api/superadmin/create-superadmin", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/superadmin/users"] });
      setIsCreateOpen(false);
      form.reset();
      toast({
        title: "User Created",
        description: "The admin user has been created successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Creation Failed",
        description: error.message || "Failed to create user",
        variant: "destructive",
      });
    },
  });

  const toggleUserMutation = useMutation({
    mutationFn: async ({ id, isActive }: { id: string; isActive: boolean }) => {
      return apiRequest("PATCH", `/api/superadmin/users/${id}/status`, { isActive });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/superadmin/users"] });
      toast({
        title: "User Updated",
        description: "The user status has been updated.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update user",
        variant: "destructive",
      });
    },
  });

  const deleteUserMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/superadmin/users/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/superadmin/users"] });
      toast({
        title: "User Deleted",
        description: "The user has been removed.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Delete Failed",
        description: error.message || "Failed to delete user",
        variant: "destructive",
      });
    },
  });

  const getRoleIcon = (role: string) => {
    switch (role) {
      case "superadmin":
        return <ShieldCheck size={16} className="text-pink-400" />;
      case "admin":
        return <Shield size={16} className="text-cyan-400" />;
      case "tech":
        return <Wrench size={16} className="text-amber-400" />;
      default:
        return <Users size={16} />;
    }
  };

  const getRoleBadge = (role: string) => {
    const roleConfig: Record<string, { bg: string; text: string; label: string }> = {
      superadmin: { bg: "bg-pink-500/20", text: "text-pink-400", label: "Super Admin" },
      admin: { bg: "bg-cyan-500/20", text: "text-cyan-400", label: "Admin" },
      tech: { bg: "bg-amber-500/20", text: "text-amber-400", label: "Technician" },
    };
    const config = roleConfig[role] || roleConfig.admin;
    return (
      <Badge variant="outline" className={`${config.bg} ${config.text} border-0`}>
        {config.label}
      </Badge>
    );
  };

  const onSubmit = (data: CreateUserForm) => {
    createUserMutation.mutate(data);
  };

  return (
    <div className="p-6 space-y-6" data-testid="superadmin-users-page">
      <div className="flex items-center justify-between gap-4 flex-wrap">
        <div>
          <h1 className="text-2xl font-bold text-white">User Management</h1>
          <p className="text-muted-foreground">
            Create and manage platform administrators and sub-admins.
          </p>
        </div>

        <Dialog open={isCreateOpen} onOpenChange={setIsCreateOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-user">
              <UserPlus size={16} className="mr-2" />
              Create Admin
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-md">
            <DialogHeader>
              <DialogTitle>Create Admin User</DialogTitle>
              <DialogDescription>
                Add a new administrator or technician to the platform.
              </DialogDescription>
            </DialogHeader>

            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="username"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Username</FormLabel>
                      <FormControl>
                        <Input
                          placeholder="johndoe"
                          {...field}
                          data-testid="input-username"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <Input
                          type="email"
                          placeholder="john@example.com"
                          {...field}
                          data-testid="input-email"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <Input
                          type="password"
                          placeholder="Minimum 6 characters"
                          {...field}
                          data-testid="input-password"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="role"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Role</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-role">
                            <SelectValue placeholder="Select a role" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="superadmin">
                            <div className="flex items-center gap-2">
                              <ShieldCheck size={14} className="text-pink-400" />
                              Super Admin
                            </div>
                          </SelectItem>
                          <SelectItem value="admin">
                            <div className="flex items-center gap-2">
                              <Shield size={14} className="text-cyan-400" />
                              Admin
                            </div>
                          </SelectItem>
                          <SelectItem value="tech">
                            <div className="flex items-center gap-2">
                              <Wrench size={14} className="text-amber-400" />
                              Technician
                            </div>
                          </SelectItem>
                        </SelectContent>
                      </Select>
                      <FormDescription>
                        Super Admins can manage the entire platform. Admins manage their tenant. Technicians have limited access.
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <DialogFooter>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setIsCreateOpen(false)}
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    disabled={createUserMutation.isPending}
                    data-testid="button-submit"
                  >
                    {createUserMutation.isPending ? "Creating..." : "Create User"}
                  </Button>
                </DialogFooter>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      <GlassPanel size="md">
        {isLoading ? (
          <div className="space-y-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="animate-pulse flex items-center gap-4 p-4 rounded-lg bg-white/5">
                <div className="w-10 h-10 rounded-full bg-white/10" />
                <div className="flex-1 space-y-2">
                  <div className="h-4 bg-white/10 rounded w-1/4" />
                  <div className="h-3 bg-white/10 rounded w-1/6" />
                </div>
                <div className="h-6 w-20 bg-white/10 rounded" />
              </div>
            ))}
          </div>
        ) : users && users.length > 0 ? (
          <div className="space-y-3">
            {users.map((user, index) => (
              <motion.div
                key={user.id}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.05 }}
                className={`flex items-center gap-4 p-4 rounded-lg border ${
                  user.isActive
                    ? "bg-white/5 border-white/10"
                    : "bg-red-500/5 border-red-500/20"
                }`}
                data-testid={`user-row-${user.id}`}
              >
                <div className="w-10 h-10 rounded-full bg-gradient-to-br from-cyan-500/20 to-purple-500/20 flex items-center justify-center flex-shrink-0">
                  {getRoleIcon(user.role)}
                </div>

                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 flex-wrap">
                    <p className="font-semibold text-white">{user.username}</p>
                    {getRoleBadge(user.role)}
                    {!user.isActive && (
                      <Badge variant="outline" className="bg-red-500/20 text-red-400 border-0">
                        Disabled
                      </Badge>
                    )}
                  </div>
                  {user.email && (
                    <div className="flex items-center gap-1 text-sm text-muted-foreground">
                      <Mail size={12} />
                      <span>{user.email}</span>
                    </div>
                  )}
                </div>

                <div className="hidden md:flex items-center gap-4">
                  {user.tenantName && (
                    <div className="text-center">
                      <p className="text-xs text-muted-foreground">Tenant</p>
                      <p className="text-sm text-white">{user.tenantName}</p>
                    </div>
                  )}
                  <div className="text-center">
                    <div className="flex items-center gap-1 text-muted-foreground">
                      <Calendar size={14} />
                      <span className="text-xs">Created</span>
                    </div>
                    <p className="text-sm text-white">
                      {format(new Date(user.createdAt), "MMM d, yyyy")}
                    </p>
                  </div>
                </div>

                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button
                      variant="ghost"
                      size="icon"
                      data-testid={`button-user-actions-${user.id}`}
                    >
                      <MoreVertical size={18} />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    {user.isActive ? (
                      <DropdownMenuItem
                        onClick={() =>
                          toggleUserMutation.mutate({ id: user.id, isActive: false })
                        }
                        data-testid={`action-disable-${user.id}`}
                      >
                        <PowerOff size={16} className="mr-2 text-amber-400" />
                        Disable User
                      </DropdownMenuItem>
                    ) : (
                      <DropdownMenuItem
                        onClick={() =>
                          toggleUserMutation.mutate({ id: user.id, isActive: true })
                        }
                        data-testid={`action-enable-${user.id}`}
                      >
                        <Power size={16} className="mr-2 text-cyan-400" />
                        Enable User
                      </DropdownMenuItem>
                    )}
                    <DropdownMenuSeparator />
                    <DropdownMenuItem
                      onClick={() => deleteUserMutation.mutate(user.id)}
                      className="text-red-400 focus:text-red-400"
                      data-testid={`action-delete-${user.id}`}
                    >
                      <Trash2 size={16} className="mr-2" />
                      Delete User
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </motion.div>
            ))}
          </div>
        ) : (
          <div className="text-center py-12 text-muted-foreground">
            <Users size={48} className="mx-auto mb-4 opacity-50" />
            <p>No admin users found</p>
            <p className="text-sm mt-1">Create your first admin user to get started</p>
          </div>
        )}
      </GlassPanel>
    </div>
  );
}

client/src/pages/superadmin-login.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { motion } from "framer-motion";
import { User, Lock, Loader2, ArrowRight, ShieldCheck } from "lucide-react";
import { MeshBackground } from "@/components/mesh-background";
import { MnetiFiLogo } from "@/components/mnetifi-logo";
import { GlassPanel } from "@/components/glass-panel";
import { GlassInput } from "@/components/glass-input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";

export default function SuperAdminLoginPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const session = localStorage.getItem("superadmin_session");
    if (session) {
      try {
        const sessionData = JSON.parse(session);
        const lastActivity = new Date(sessionData.lastActivity).getTime();
        const now = Date.now();
        const tenMinutes = 10 * 60 * 1000;

        if (now - lastActivity < tenMinutes && sessionData.user?.role === "superadmin") {
          setLocation("/superadmin");
        } else {
          localStorage.removeItem("superadmin_session");
        }
      } catch {
        localStorage.removeItem("superadmin_session");
      }
    }
  }, [setLocation]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        if (data.user.role !== "superadmin") {
          toast({
            title: "Access Denied",
            description: "This login is for Super Admins only. Please use the ISP login.",
            variant: "destructive",
          });
          return;
        }

        localStorage.setItem("superadmin_session", JSON.stringify({
          user: data.user,
          lastActivity: new Date().toISOString(),
        }));
        toast({
          title: "Welcome, Super Admin!",
          description: `Logged in as ${data.user.username}`,
        });
        setLocation("/superadmin");
      } else {
        toast({
          title: "Login Failed",
          description: data.error || "Invalid credentials",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Login Failed",
        description: "Unable to connect to server",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <MeshBackground />
      <div className="min-h-screen flex items-center justify-center p-4 relative z-10">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="w-full max-w-md"
        >
          <GlassPanel size="lg" className="text-center">
            <div className="mb-8">
              <MnetiFiLogo size="lg" className="mx-auto mb-4" />
              <div className="flex items-center justify-center gap-2 mb-2">
                <Badge variant="outline" className="bg-pink-500/20 text-pink-400 border-0">
                  <ShieldCheck size={12} className="mr-1" />
                  Platform Administration
                </Badge>
              </div>
              <h1 className="text-2xl font-bold text-white mb-2">Super Admin Login</h1>
              <p className="text-muted-foreground">
                Sign in to manage the MnetiFi platform
              </p>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <GlassInput
                label="Username"
                placeholder="Enter your admin username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                icon={<User size={16} />}
                data-testid="input-superadmin-username"
              />

              <GlassInput
                label="Password"
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                icon={<Lock size={16} />}
                data-testid="input-superadmin-password"
              />

              <Button
                type="submit"
                className="w-full bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700"
                disabled={isLoading}
                data-testid="button-superadmin-login"
              >
                {isLoading ? (
                  <>
                    <Loader2 size={18} className="mr-2 animate-spin" />
                    Authenticating...
                  </>
                ) : (
                  <>
                    Access Platform Console
                    <ArrowRight size={18} className="ml-2" />
                  </>
                )}
              </Button>
            </form>

            <div className="mt-4 text-center space-y-2">
              <Link 
                href="/superadmin/forgot-password" 
                className="text-sm text-pink-400 hover:text-pink-300 transition-colors block"
                data-testid="link-superadmin-forgot-password"
              >
                Forgot your password?
              </Link>
              <Link 
                href="/superadmin/register" 
                className="text-sm text-purple-400 hover:text-purple-300 transition-colors block"
                data-testid="link-superadmin-register"
              >
                Register as Super Admin
              </Link>
            </div>

            <div className="mt-6 pt-6 border-t border-white/10">
              <p className="text-sm text-muted-foreground">
                ISP Administrator?{" "}
                <Link 
                  href="/login" 
                  className="text-cyan-400 hover:text-cyan-300 transition-colors font-medium"
                  data-testid="link-isp-login"
                >
                  Go to ISP Login
                </Link>
              </p>
            </div>

            <div className="mt-4 p-3 rounded-lg bg-pink-500/10 border border-pink-500/20 text-left">
              <p className="text-xs text-pink-200/80">
                <ShieldCheck size={14} className="inline mr-1" />
                This portal is for MnetiFi platform administrators only. Unauthorized access attempts are logged.
              </p>
            </div>
          </GlassPanel>
        </motion.div>
      </div>
    </>
  );
}

client/src/pages/superadmin-tenant-detai.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute, Link } from "wouter";
import { motion } from "framer-motion";
import { format } from "date-fns";
import {
  Building2,
  ArrowLeft,
  Users,
  DollarSign,
  Calendar,
  Clock,
  CheckCircle,
  Pause,
  Ban,
  Power,
  RefreshCw,
  Wifi,
  Router,
  CreditCard,
  Activity,
  TrendingUp,
} from "lucide-react";
import { GlassPanel } from "@/components/glass-panel";
import { MetricCard, MetricCardSkeleton } from "@/components/metric-card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface TenantDetails {
  id: string;
  name: string;
  subdomain: string;
  email: string | null;
  phone: string | null;
  tier: string;
  isActive: boolean;
  saasBillingStatus: string;
  trialExpiresAt: string | null;
  subscriptionExpiresAt: string | null;
  monthlyRevenue: number;
  totalUsers: number;
  createdAt: string;
  userCount: number;
  transactionCount: number;
  hotspotCount: number;
  planCount: number;
  revenueThisMonth: number;
  revenueLastMonth: number;
}

export default function SuperAdminTenantDetailsPage() {
  const [, params] = useRoute("/superadmin/tenants/:id");
  const tenantId = params?.id;
  const { toast } = useToast();

  const { data: tenant, isLoading } = useQuery<TenantDetails>({
    queryKey: ["/api/superadmin/tenants", tenantId],
    enabled: !!tenantId,
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ isActive, saasBillingStatus }: { isActive?: boolean; saasBillingStatus?: string }) => {
      return apiRequest("PATCH", `/api/superadmin/tenants/${tenantId}/status`, {
        isActive,
        saasBillingStatus,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/superadmin/tenants", tenantId] });
      queryClient.invalidateQueries({ queryKey: ["/api/superadmin/tenants"] });
      toast({ title: "Tenant Updated", description: "Status has been updated successfully." });
    },
    onError: (error: Error) => {
      toast({ title: "Update Failed", description: error.message, variant: "destructive" });
    },
  });

  const updateTierMutation = useMutation({
    mutationFn: async (tier: string) => {
      return apiRequest("PATCH", `/api/superadmin/tenants/${tenantId}/subscription`, { tier });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/superadmin/tenants", tenantId] });
      queryClient.invalidateQueries({ queryKey: ["/api/superadmin/tenants"] });
      toast({ title: "Tier Updated", description: "Subscription tier has been updated." });
    },
    onError: (error: Error) => {
      toast({ title: "Update Failed", description: error.message, variant: "destructive" });
    },
  });

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat("en-KE", {
      style: "currency",
      currency: "KES",
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const getStatusBadge = (status: string) => {
    const config: Record<string, { bg: string; text: string; label: string }> = {
      ACTIVE: { bg: "bg-cyan-500/20", text: "text-cyan-400", label: "Active" },
      TRIAL: { bg: "bg-purple-500/20", text: "text-purple-400", label: "Trial" },
      SUSPENDED: { bg: "bg-amber-500/20", text: "text-amber-400", label: "Suspended" },
      BLOCKED: { bg: "bg-red-500/20", text: "text-red-400", label: "Blocked" },
    };
    const c = config[status] || config.TRIAL;
    return <Badge variant="outline" className={`${c.bg} ${c.text} border-0`}>{c.label}</Badge>;
  };

  const getTierBadge = (tier: string) => {
    const config: Record<string, { bg: string; text: string }> = {
      TRIAL: { bg: "bg-purple-500/20", text: "text-purple-400" },
      TIER_1: { bg: "bg-cyan-500/20", text: "text-cyan-400" },
      TIER_2: { bg: "bg-pink-500/20", text: "text-pink-400" },
    };
    const c = config[tier] || config.TRIAL;
    return <Badge variant="outline" className={`${c.bg} ${c.text} border-0`}>{tier.replace("_", " ")}</Badge>;
  };

  if (isLoading) {
    return (
      <div className="p-6 space-y-6">
        <div className="animate-pulse h-8 w-64 bg-white/10 rounded" />
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <MetricCardSkeleton />
          <MetricCardSkeleton />
          <MetricCardSkeleton />
          <MetricCardSkeleton />
        </div>
      </div>
    );
  }

  if (!tenant) {
    return (
      <div className="p-6">
        <div className="text-center py-12">
          <Building2 size={48} className="mx-auto mb-4 opacity-50 text-muted-foreground" />
          <p className="text-muted-foreground">Tenant not found</p>
          <Link href="/superadmin/tenants">
            <Button variant="outline" className="mt-4">Back to Tenants</Button>
          </Link>
        </div>
      </div>
    );
  }

  const revenueGrowth = tenant.revenueLastMonth > 0
    ? Math.round(((tenant.revenueThisMonth - tenant.revenueLastMonth) / tenant.revenueLastMonth) * 100)
    : 0;

  return (
    <div className="p-6 space-y-6" data-testid="tenant-details-page">
      <div className="flex items-center gap-4 flex-wrap">
        <Link href="/superadmin/tenants">
          <Button variant="ghost" size="icon" data-testid="button-back">
            <ArrowLeft size={18} />
          </Button>
        </Link>
        <div className="flex-1">
          <div className="flex items-center gap-3 flex-wrap">
            <h1 className="text-2xl font-bold text-white">{tenant.name}</h1>
            {getTierBadge(tenant.tier)}
            {getStatusBadge(tenant.saasBillingStatus)}
          </div>
          <p className="text-muted-foreground">{tenant.subdomain}</p>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <MetricCard
          title="WiFi Users"
          value={tenant.userCount}
          icon={<Users size={24} />}
          gradient="primary"
        />
        <MetricCard
          title="This Month Revenue"
          value={formatCurrency(tenant.revenueThisMonth)}
          icon={<DollarSign size={24} />}
          trend={revenueGrowth !== 0 ? { value: revenueGrowth, label: "vs last month" } : undefined}
          gradient="mpesa"
        />
        <MetricCard
          title="Transactions"
          value={tenant.transactionCount}
          icon={<CreditCard size={24} />}
        />
        <MetricCard
          title="Hotspots"
          value={tenant.hotspotCount}
          icon={<Router size={24} />}
          gradient="accent"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <GlassPanel size="md">
          <h2 className="text-lg font-semibold text-white mb-4">Tenant Information</h2>
          <div className="space-y-4">
            <div className="flex justify-between gap-4">
              <span className="text-muted-foreground">Email</span>
              <span className="text-white">{tenant.email || "Not set"}</span>
            </div>
            <div className="flex justify-between gap-4">
              <span className="text-muted-foreground">Phone</span>
              <span className="text-white">{tenant.phone || "Not set"}</span>
            </div>
            <div className="flex justify-between gap-4">
              <span className="text-muted-foreground">Created</span>
              <span className="text-white">{format(new Date(tenant.createdAt), "PPP")}</span>
            </div>
            <div className="flex justify-between gap-4">
              <span className="text-muted-foreground">Active Plans</span>
              <span className="text-white">{tenant.planCount}</span>
            </div>
            {tenant.trialExpiresAt && (
              <div className="flex justify-between gap-4">
                <span className="text-muted-foreground">Trial Expires</span>
                <span className="text-amber-400">{format(new Date(tenant.trialExpiresAt), "PPP")}</span>
              </div>
            )}
            {tenant.subscriptionExpiresAt && (
              <div className="flex justify-between gap-4">
                <span className="text-muted-foreground">Subscription Expires</span>
                <span className="text-white">{format(new Date(tenant.subscriptionExpiresAt), "PPP")}</span>
              </div>
            )}
          </div>
        </GlassPanel>

        <GlassPanel size="md">
          <h2 className="text-lg font-semibold text-white mb-4">Quick Actions</h2>
          <div className="space-y-4">
            <div>
              <label className="text-sm text-muted-foreground mb-2 block">Change Tier</label>
              <Select
                value={tenant.tier}
                onValueChange={(tier) => updateTierMutation.mutate(tier)}
              >
                <SelectTrigger className="bg-white/5 border-white/10" data-testid="select-tier">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="TRIAL">Trial (Free)</SelectItem>
                  <SelectItem value="TIER_1">Tier 1 (Ksh 500/month)</SelectItem>
                  <SelectItem value="TIER_2">Tier 2 (Ksh 1,500/month)</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="flex flex-wrap gap-2">
              {tenant.saasBillingStatus !== "ACTIVE" && (
                <Button
                  onClick={() => updateStatusMutation.mutate({ isActive: true, saasBillingStatus: "ACTIVE" })}
                  className="flex-1"
                  disabled={updateStatusMutation.isPending}
                  data-testid="button-activate"
                >
                  <CheckCircle size={16} className="mr-2" />
                  Activate
                </Button>
              )}
              {tenant.saasBillingStatus !== "SUSPENDED" && (
                <Button
                  variant="outline"
                  onClick={() => updateStatusMutation.mutate({ isActive: false, saasBillingStatus: "SUSPENDED" })}
                  className="flex-1"
                  disabled={updateStatusMutation.isPending}
                  data-testid="button-suspend"
                >
                  <Pause size={16} className="mr-2" />
                  Suspend
                </Button>
              )}
              {tenant.saasBillingStatus !== "BLOCKED" && (
                <Button
                  variant="destructive"
                  onClick={() => updateStatusMutation.mutate({ isActive: false, saasBillingStatus: "BLOCKED" })}
                  className="flex-1"
                  disabled={updateStatusMutation.isPending}
                  data-testid="button-block"
                >
                  <Ban size={16} className="mr-2" />
                  Block
                </Button>
              )}
            </div>
          </div>
        </GlassPanel>
      </div>

      <GlassPanel size="md">
        <div className="flex items-center justify-between mb-4 gap-4 flex-wrap">
          <h2 className="text-lg font-semibold text-white">Revenue Overview</h2>
          <div className="flex items-center gap-2">
            <TrendingUp size={16} className="text-cyan-400" />
            <span className="text-sm text-muted-foreground">Monthly Performance</span>
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <motion.div
            className="p-4 rounded-lg bg-white/5 border border-white/10"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <p className="text-sm text-muted-foreground mb-1">This Month</p>
            <p className="text-2xl font-bold text-white">{formatCurrency(tenant.revenueThisMonth)}</p>
          </motion.div>
          <motion.div
            className="p-4 rounded-lg bg-white/5 border border-white/10"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 }}
          >
            <p className="text-sm text-muted-foreground mb-1">Last Month</p>
            <p className="text-2xl font-bold text-white">{formatCurrency(tenant.revenueLastMonth)}</p>
          </motion.div>
          <motion.div
            className="p-4 rounded-lg bg-white/5 border border-white/10"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
          >
            <p className="text-sm text-muted-foreground mb-1">Growth</p>
            <p className={`text-2xl font-bold ${revenueGrowth >= 0 ? "text-cyan-400" : "text-red-400"}`}>
              {revenueGrowth >= 0 ? "+" : ""}{revenueGrowth}%
            </p>
          </motion.div>
        </div>
      </GlassPanel>
    </div>
  );
}

client/src/pages/superadmin-register.tsx.content:
import { useState } from "react";
import { useLocation, Link } from "wouter";
import { motion } from "framer-motion";
import { Shield, User, Mail, Lock, Eye, EyeOff, ArrowRight, CheckCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

export default function SuperAdminRegisterPage() {
